  showLogoModel:function(){
       wx.showModal({
          title: '公众号搜索 " 游品慧 " ',
          content: '公众号搜索 "游品慧" 并添加关注，探寻地质地理和大自然的奥秘，了解科普实践课程情况',
          showCancel: false, //不显示取消按钮
          confirmText: '确定'
        })
  },
    
    addScale:function(){
      var that = this 
        this.mapCtx.getScale({
             success:function(res){
              var newscale = Number(res.scale)+1
               that.setData({
                 scale : newscale
             });
            }
        })
    },
    minScale:function(){
        var that = this 
        this.mapCtx.getScale({
                 success:function(res){
                  var newscale = Number(res.scale)-1
                   that.setData({
                     scale : newscale
                 });
              }
          })
    },
    // 获取缩放等级
    getScale:function(e){ 
       var that = this 
       if(e.causedBy=="scale"){
            this.mapCtx.getScale({
               success:function(res){
                  if(res.scale >= 12){
                     var list = that.data.markers
                     for(var i=0;i<list.length;i++){
                          var i = Number(i)
                          list[i].callout.display = "ALWAYS"
                          list[i].callout.color = "#009688"
                      }
                      that.setData({
                          markers : list
                      });
                  }
                  else{
                      var list = that.data.markers
                      for(var i=0;i<list.length;i++){
                            var i = Number(i)
                            list[i].callout.display = "BYCLICK"
                         }
                        that.setData({
                            markers : list
                       });
                   }
                 }
            })
       }
    },
