    var that = this;
    var xzqh = that.data.array[e.detail.value];
    //console.log(xzqh)
   this.setData({
       index: e.detail.value,
       areas: [xzqh],
       value: [0,0,0],
       markers : "",
       hasStation:true,
       areaInfo: '请选择科普研学基地！',
    })

   //通过行政区划查询场馆列表
   var url= ''+pathURL+'/wx/basedetails/listtype'  
   wx.request({
        url: url,
        method:"GET",
        data:{
            district:xzqh
        },
        header: {
           'content-type': 'application/json'
        },
        success:function(res){
            var list = res.data;  
           // console.log(res)
          if(list == "" || list == undefined || list == null){
               wx.showModal({
                  title: '',
                  content: '未查到相关信息！',
                  showCancel: false, //不显示取消按钮
                  confirmText: '确定'
                })
                that.setData({
                    markers : "",
                    hasStation:false
                });
             }
             else{
                that.setData({
                    markers : list
              });
           }
          },
          fail:function(res){
            console.log(res)
          }
      });

