   //点击所在地区弹出选择框
    select: function (e) {
      var that = this;
      that.setData({
           LOGO : false
        })
        // 如果已经显示，不在执行显示动画
        if (this.data.addressMenuIsShow) {
           return false
        } 
        else {
            // 执行显示动画
            this.startAddressAnimation(true);

          //默认第一个区域选择
          const areaChoose = that.data.areas[0]

          const url= ''+pathURL+'/wx/district/listtype'
           wx.request({
                  url: url,
                  method:"GET",
                  data:{
                    limit:9999,
                    district:areaChoose
                  },
                  header: {
                   'content-type': 'application/json'
                  },
                  success:function(res){
                    that.setData({
                       kinds : res.data
                    })
                   //console.log(that.data.kinds)

                //默认第一种类型
                var typeChoose = that.data.kinds[0]
                const url= ''+pathURL+'/wx/district/list';
                wx.request({
                       url: url,
                       method:"GET",
                        data:{
                            limit:9999,
                            district:areaChoose,
                            type:typeChoose
                        },
                       header: {
                        'content-type': 'application/json'
                       },
                       success:function(res){
                             that.setData({
                               stations : res.data.page.list
                             })
                         },
                         fail:function(res){
                           console.log(res)
                         }
                     });
                    },
                    fail:function(res){
                      console.log(res)
                    }
                });
        }
    },
    // 点击选择执行动画
    startAddressAnimation: function (isShow) {
         if (isShow) {
            // vh是用来表示尺寸的单位，高度全屏是100vh
            this.animation.translateY(0 + 'vh').step()
         } 
         else {
            this.animation.translateY(50 + 'vh').step()
         }
        this.setData({
            animationAddressMenu: this.animation.export(),
            addressMenuIsShow: isShow,
        })
    },
    // 点击地区选择取消按钮
    stationCancel: function (e) {
        this.startAddressAnimation(false)
        this.setData({
           LOGO : true
        })
    },
    // 点击基地选择确定按钮
    stationSure: function (e) {
        var that = this
        var value = that.data.value
         that.setData({
           LOGO : true
        })
        this.startAddressAnimation(false)
        // 将选择的基地信息显示到输入框
        var areaInfo = that.data.stations[value[2]].educationBase
        that.setData({
            areaInfo: areaInfo,
            searchVal:that.data.stations[value[2]].educationBase
        })
     //console.log(that.data.searchVal)
  //通过ID查询场馆详情
   const url= ''+pathURL+'/wx/basedetails/listtype';
   wx.request({
        url: url,
        method:"GET",
        data:{
            name:that.data.searchVal
        },
        header: {
         'content-type': 'application/json'
        },
        success:function(res){
            var list = res.data;  
            //console.log(res)
           if(list == "" || list == undefined || list == null){
               wx.showModal({
                  title: '',
                  content: '未查到相关信息！',
                  showCancel: false, //不显示取消按钮
                  confirmText: '确定'
                })
                that.setData({
                    markers : "",
                });
             }
             else{
                list[0].callout.display = "ALWAYS"
                list[0].callout.color = "#009688"
                that.setData({
                    markers : list,
                    latitude: list[0].latitude,
                    longitude: list[0].longitude,
              });
           }
          },
          fail:function(res){
            console.log(res)
          }
      });
        
    },
    // 处理联动逻辑
    stationChange: function (e) {
        var that = this
        var value = e.detail.value
        var areas = this.data.areas
        var areaChoose = that.data.areas[value[0]]
        var areaNum = value[0]
        var kindNum = value[1]          
        var stationNum = value[2]
    
        if (this.data.value[0] != areaNum) {
         //console.log("滑动选择了第一项")
         that.setData({
            kinds : [],
            stations : []
         })
         //console.log(areaChoose)
         const url= ''+pathURL+'/wx/district/listtype'
         wx.request({
             url: url,
             method:"GET",
             data:{
                limit:9999,
                district:areaChoose
            },
             header: {
              'content-type': 'application/json'
             },
             success:function(res){
                   that.setData({
                     kinds : res.data,
                     value: [areaNum, 0, 0],
                   })
                 // console.log(that.data.kinds)
                var typeChoose = that.data.kinds[0]

                const url= ''+pathURL+'/wx/district/list'
                wx.request({
                       url: url,
                       method:"GET",
                       data:{
                            limit:9999,
                            district:areaChoose,
                            type:typeChoose
                        },
                       header: {
                        'content-type': 'application/json'
                       },
                       success:function(res){
                             that.setData({
                               stations : res.data.page.list,
                               value: [areaNum, kindNum, 0],
                             })
                         },
                         fail:function(res){
                           console.log(res)
                         }
                     });
          },
          fail:function(res){
            console.log(res)
          }
      });
     } 
        // 滑动选择了第二项数据
        else if (this.data.value[1] != kindNum) {
          //console.log(that.data.kinds)
          var typeChoose = that.data.kinds[value[1]]

           const url= ''+pathURL+'/wx/district/list'
           wx.request({
                  url: url,
                  method:"GET",
                  data:{
                    limit:9999,
                    district:areaChoose,
                    type:typeChoose
                  },
                  header: {
                   'content-type': 'application/json'
                  },
                  success:function(res){
                   // console.log(res)
                        that.setData({
                          stations : res.data.page.list,
                          value: [areaNum, kindNum, 0],
                        })
                    },
                    fail:function(res){
                      console.log(res)
                    }
                });

        } 
        // 滑动选择了第三项数据
        else {
            //console.log(that.data.stations[value[2]].educationBase)
            this.setData({
                value: [areaNum, kindNum, stationNum],
            })
        }
    },

